DROP TABLE IF EXISTS `achievement_dbc`;
CREATE TABLE `achievement_dbc` (
	`ID` BIGINT(20) NOT NULL DEFAULT '0',
	`Faction` BIGINT(20) NOT NULL DEFAULT '0',
	`Map` BIGINT(20) NOT NULL DEFAULT '0',
	`Previous` BIGINT(20) NOT NULL DEFAULT '0',
	`Name` TEXT NOT NULL,
	`Description` TEXT NOT NULL,
	`Category` BIGINT(20) NOT NULL DEFAULT '0',
	`Points` BIGINT(20) NOT NULL DEFAULT '0',
	`OrderInGroup` BIGINT(20) NOT NULL DEFAULT '0',
	`Flags` BIGINT(20) NOT NULL DEFAULT '0',
	`SpellIcon` BIGINT(20) NOT NULL DEFAULT '0',
	`Reward` TEXT NOT NULL,
	`Demands` TEXT NOT NULL,
	`ReferencedAchievement` BIGINT(20) NOT NULL DEFAULT '0',
	PRIMARY KEY (`ID`)
) ENGINE=MYISAM DEFAULT CHARSET=utf8 COMMENT='Export of Achievement.dbc';

